var stringResult = null;

if(valueSeparator != null) {
    var blockValues = stcw.getBlockValuesAsArray(parentDocId);
    if(blockValues != null && blockValues.length > 0) {
        stringResult = "";
        for(var i=0; i<blockValues.length; i++) {
            if(i>0 && stringResult != "") {
                stringResult = stringResult + valueSeparator;
            }
            if(blockValues[i].blockValue != null) {
                stringResult = stringResult + blockValues[i].blockValue;
            }
        }
    }
}

return stringResult;
