script.script:
if (order == null) {
      Global.logDebug ("getInterfaceFolderCount (): order is required");
      return (null);
}

if (interfaceName == null) {
      Global.logDebug ("getInterfaceFolderCount (): interfaceName is required");
      return (null);
}

if (versionNumber == null)
      versionNumber = order.versions.count - 1;

if (versionNumber < 0) {
      Global.logDebug ("getInterfaceFolderCount (): version number must be non-negative");
      return (null);
}

var orderVersion = order.versions [versionNumber];
if (orderVersion.interfaces == null)
      return (0);

// return (orderVersion.interfaces.count);
var foundPosition = -1;

// start from the last interface and come back until it found the latest interface;
for(var i=orderVersion.interfaces.count-1; i>=0 && foundPosition < 0; i--) {
      if(orderVersion.interfaces[i].interfaceData.interfaceName == interfaceName) {
            foundPosition = i + 1;
      }
}

if(foundPosition < 0)
      // interface not found; returning last position
      foundPosition = orderVersion.interfaces.count;

return (foundPosition);
