var resultLineItem = null;

var inputDoc = new Document("stcw.STCW_ORDER_ORCHESTRATION");
inputDoc.cwOrderId = orderId;
inputDoc.cwDocId = null;
inputDoc.lineItemIdentifier = childLineItemIdentifier;

var orchestrationRecordList = Finder.runFinder("stcw.findSTCW_ORDER_ORCHESTRATION", "select", inputDoc);

if(orchestrationRecordList != null && orchestrationRecordList.length > 0) {
    resultLineItem = Document.readDoc("stcw.lineItemDocument", orchestrationRecordList[0].cwDocId);

}
return resultLineItem;
