script.script:
var inputDoc = new Document("stcconf.search_ValidationMap_ServiceType_OrderTypeDoc");
inputDoc.orderType = orderType;
inputDoc.serviceType = serviceType;

var resultList = Finder.runFinder("stcconf.findMandatoryNVPairsByOrderType_ServiceType", "select", inputDoc);

if(resultList == null || resultList.length == 0) {
    // found nothing; searching all the mandatory NV pairs for the orderType and all the services
    inputDoc.serviceType = "*";

    resultList = Finder.runFinder("stcconf.findMandatoryNVPairsByOrderType_ServiceType", "select", inputDoc);
}

return resultList;
