var nvPairDependencies = null;

if(orderType != null && serviceType != null) {
    var inputDoc = new Document("stcconf.search_ValidationMap_ServiceType_OrderTypeDoc");
    inputDoc.orderType = orderType;
    inputDoc.serviceType = serviceType;

    var resultList = Finder.runFinder("stcconf.findNVPairDependencies", "select", inputDoc);

    if(resultList != null && resultList.length > 0) {
        nvPairDependencies = resultList;
    }
}

return nvPairDependencies;
