script.script:
var inputDoc = new Document("stcconf.search_operation_LTE");
inputDoc.step       = stepInWorkflow;
inputDoc.orderType = orderType;
inputDoc.isSubmit = isSubmit;
inputDoc.changeRequestType = (orderType == "C" ?       changeRequestType : null);

var resultList = Finder.runFinder("stcconf.findOperationForLTEConfiguration", "select", inputDoc);

var operation = null;

if(resultList != null && resultList.length > 0) {
    operation = resultList[0];
}
return operation;
