script.script:
var inputDoc = new Document("stcconf.search_priorityMapping");
inputDoc.orderType = orderType;
inputDoc.serviceType = serviceType;
inputDoc.productCode = productCode;
inputDoc.reasonCode = reasonCode;

var resultList = Finder.runFinder("stcconf.findPriorityMapping", "select", inputDoc);

var priorityMapping = null;

if(resultList != null && resultList.length > 0) {
    priorityMapping = resultList[0];
}
else {
    // searching with wildcard
    inputDoc.reasonCode = "*";

    resultList = Finder.runFinder("stcconf.findPriorityMapping", "select", inputDoc);

    if(resultList != null && resultList.length > 0) {
        priorityMapping = resultList[0];
    }
}

return priorityMapping;
