var inputDoc = new Document("stcw.STCW_ORDER_ORCHESTRATION");
inputDoc.orderNumber = orderNumber;
inputDoc.cwOrderId = cwOrderId;
inputDoc.lineItemIdentifier = lineItemIdentifier;

var orchestrationRecord = null;

