script.script:
var inputDoc = new Document("stcconf.search_NVPairName");
inputDoc.name = inputName;
inputDoc.serviceType = inputServiceType;

var isInList = false;

var resultList = Finder.runFinder("stcconf.findStoreAndForwardNVPair", "select", inputDoc);
if(resultList != null && resultList.length > 0) {
    isInList = true;
}

if(!isInList && inputServiceType != "*") {
    inputDoc.serviceType = "*";
    resultList = Finder.runFinder("stcconf.findStoreAndForwardNVPair", "select", inputDoc);
    if(resultList != null && resultList.length > 0) {
        isInList = true;
    }
}

return isInList;
