script.script:
var debugMsg = " isParentInServiceTypeHierarchy - ";

debugPrintln(stcc.getSysdateForLog() + debugMsg + "(" + serviceType + ")");

var searchDoc = new Document("stcconf.search_serviceTypesHierarchy");
searchDoc.parentServiceType = serviceType;

var resultList = Finder.runFinder("stcconf.findServiceTypeHierarchyConf", "select", searchDoc);

var isParent = (resultList != null && resultList.length > 0);

debugPrintln(stcc.getSysdateForLog() + debugMsg + "ServiceType <" + serviceType + "> " + (isParent ? "is" : "is not") + " parentService in hierarchy");

return isParent;
