script.script:
var inputDS = new DataStructure("nsmChangeSIMDetailsWS.changeSIMDetails_el");

stcw.fillHeaderForOrderACK_WOSU(inputDS.changeSIMDetailsRequest.header, "ChangeSIMDetails", "EOC");
inputDS.changeSIMDetailsRequest.header.domainId = "GSM";


fillRequestHeader_(stcw.getMappingFromCOMToLTEData("NSM", "CHANGE_SIM", null, "RH"));

var itemInDS = fillItem_(stcw.getMappingFromCOMToLTEData("NSM", "CHANGE_SIM", null, "LI"));
if(itemInDS != null) {
    inputDS.changeSIMDetailsRequest.body.requestData.numberSIMDetails.item[0] = itemInDS;
}

return inputDS;


function fillRequestHeader_(mappingForRequestHeader) {
    if(mappingForRequestHeader != null) {
        for(var i=0; i<mappingForRequestHeader.length; i++) {
            var mapping = mappingForRequestHeader[i];

            if(mapping.defaultValue != null) {
                inputDS.changeSIMDetailsRequest.body.requestHeader[mapping.ifName] = mapping.defaultValue;
            }
            else {
                if(mapping.comElement == "OH") {
                    inputDS.changeSIMDetailsRequest.body.requestHeader[mapping.ifName] = bundleOrder.orderHeader[mapping.comName];
                }
                else if(mapping.comElement == "LI") {
                    inputDS.changeSIMDetailsRequest.body.requestHeader[mapping.ifName] = orderItem[mapping.comName];
                }
                else if (mapping.comElement == "NV") {
                    inputDS.changeSIMDetailsRequest.body.requestHeader[mapping.ifName] = getNVPairValueByName_(mapping.comName);
                }
            }
        }
    }
}



function fillItem_(mappingForLineItem) {
    var item = new DataStructure("nsmChangeSIMDetailsWS.item");
    if(mappingForLineItem != null) {
        for(var i=0; i<mappingForLineItem.length; i++) {
            var mapping = mappingForLineItem[i];

            if(mapping.defaultValue != null) {
                item[mapping.ifName] = mapping.defaultValue;
            }
            else {
                if(mapping.comElement == "OH") {
                    item[mapping.ifName] = bundleOrder.orderHeader[mapping.comName];
                }
                else if(mapping.comElement == "LI") {
                    item[mapping.ifName] = orderItem[mapping.comName];
                }
                else if (mapping.comElement == "NV") {
                    item[mapping.ifName] = getNVPairValueByName_(mapping.comName);
                }
            }
        }
    }

    return item;
}

function getNVPairValueByName_(nvPairName) {
    var nvPairValue = null;
    var allNVPairsWithName = stcw.getAllNameValuesByParentElementIdAndNameAndVASFlag(orderItem.id, false, nvPairName);
    if(allNVPairsWithName != null && allNVPairsWithName.length > 0) {
        nvPairValue = allNVPairsWithName[0].value;
    }

    return nvPairValue;
}
