script.script:
var mandatoryAttributes = stcw.getLineItemMandatoryAttribsByOrderType_ServiceType(orderHeader.orderType);

var validationErrors = null;

var skipServiceNumber = ((lineItem.elementTypeInOrderTree == "B" && !isLegacy) || lineItem.action == "A");

if(mandatoryAttributes != null) {
    for(var i=0; i<mandatoryAttributes.length; i++) {
        if(mandatoryAttributes[i] != "serviceNumber" || !skipServiceNumber) {

            var attributeValue = lineItem[mandatoryAttributes[i]];
            if(!stcc.stringHasValue(attributeValue)) {
                if(validationErrors == null) {
                    validationErrors = new Array();
                }

                // AE0039: Missing Mandatory Attribute: {0}
                validationErrors.push("AE0039");
                validationErrors.push(Global.translateText("AE0039", null, [mandatoryAttributes[i]]));
            }
        }
    }
}

if(isLegacy || lineItem.elementTypeInOrderTree != "B") {
    var segmentFlag = lineItem.segmentFlag;
    if(! stcc.stringHasValue(segmentFlag)) {
        validationErrors.push("AE0039");
        validationErrors.push(Global.translateText("DE1104", null, ["segmentFlag"]));
    }
}

if(validationErrors != null) {
debugPrintln(stcc.getSysdateForLog() + " performValidationOfMandatoryAttributesInLineItem (" + lineItem.lineItemIdentifier + "," + orderHeader.orderNumber + "): Found errors [" + validationErrors + "]");
    failureArray = stcw.addValidationErrorsToFailureArrayForSub_UpdInWD(validationErrors, lineItem.lineItemIdentifier, failureArray, isSubmit);
}

return failureArray;
