script.script:
var inputDoc = new Document("stcw.STCW_GRANITE_EVENT_STORAGE");
inputDoc.cwDocId = wosuId;
inputDoc.msgType = "WOSU";

var graniteResponseDS = null;
var graniteResponseFound = Finder.runFinder("stcw.findSTCW_GRANITE_EVENT_STORAGE", "select", inputDoc);
if(graniteResponseFound != null && graniteResponseFound.length > 0) {
    var xmlMessage = graniteResponseFound[0].graniteMsg;
    var convertedList = Global.getItemsFromXML(xmlMessage, "stcw.wosuUnifiedModel");
    if (convertedList != null && convertedList.length > 0) {
        graniteResponseDS = convertedList[0];
    }

}
return graniteResponseDS;
