try {
    var inputList = Global.getItemsFromXML (xmlInput, structureName);
    if (inputList == null || inputList.length <= 0) {
debugPrintln(stcc.getSysdateForLog() + " readStructure - inputList = null!");
        return (null);
    }

     var item = inputList [0];
     return (item);
}
catch (ex) {
