script.script:
// nv check; it loops in existing NV pair and verifies if it exists also in received one
if(receivedLineItemContainer.serviceLineItemNameValueParameters != null) {
    for(var i=0; i<receivedLineItemContainer.serviceLineItemNameValueParameters.length; i++) {
        var receivedNVName = receivedLineItemContainer.serviceLineItemNameValueParameters[i].serviceLineItemNameValueParameter.name;
        var found = false;

        if(existingLineItemContainer.serviceLineItemNameValueParameters != null) {
            for(var j=0; j<existingLineItemContainer.serviceLineItemNameValueParameters.length && !found; j++) {
                if(receivedNVName == existingLineItemContainer.serviceLineItemNameValueParameters[j].serviceLineItemNameValueParameter.name) {
                    // found; comparing the values
                    found = true;
                    existingLineItemContainer.serviceLineItemNameValueParameters[j].serviceLineItemNameValueParameter.value = receivedLineItemContainer.serviceLineItemNameValueParameters[i].serviceLineItemNameValueParameter.value;
                    existingLineItemContainer.serviceLineItemNameValueParameters[j].serviceLineItemNameValueParameter.nvAction = receivedLineItemContainer.serviceLineItemNameValueParameters[i].serviceLineItemNameValueParameter.nvAction;
                }
            }
        }

        if(!found) {
            var position = (existingLineItemContainer.serviceLineItemNameValueParameters != null ? existingLineItemContainer.serviceLineItemNameValueParameters.length : 0);
            var containerItemPath = (existingLineItemContainer.serviceLineItemNameValueParameters != null ? existingLineItemContainer.serviceLineItemNameValueParameters.orderDataPath : (existingLineItemContainer.orderDataPath + ".serviceLineItemNameValueParameters"));
            var nvDoc = existingOrder.createOrderItemByPath(containerItemPath + "." + (position+1) + ".serviceLineItemNameValueParameter");
            nvDoc.name = receivedLineItemContainer.serviceLineItemNameValueParameters[i].serviceLineItemNameValueParameter.name;
            nvDoc.value = receivedLineItemContainer.serviceLineItemNameValueParameters[i].serviceLineItemNameValueParameter.value;
            nvDoc.nvAction = receivedLineItemContainer.serviceLineItemNameValueParameters[i].serviceLineItemNameValueParameter.nvAction;
            nvDoc.parentElementId = existingLineItemContainer.serviceLineItem.id;
        }
    }
}

// vas check; it loops in existing VAS pairs and verifies if it exists also in received one
if(receivedLineItemContainer.serviceLineItemVASNVParameters != null) {
    for(var i=0; i<receivedLineItemContainer.serviceLineItemVASNVParameters.length; i++) {
        var receivedNVName = receivedLineItemContainer.serviceLineItemVASNVParameters[i].serviceLineItemVASNVParameter.name;
        var found = false;

        if(existingLineItemContainer.serviceLineItemVASNVParameters != null) {
            for(var j=0; j<existingLineItemContainer.serviceLineItemVASNVParameters.length && !found; j++) {
                if(receivedNVName == existingLineItemContainer.serviceLineItemVASNVParameters[j].serviceLineItemVASNVParameter.name) {
                    // found; comparing the values
                    found = true;
                    existingLineItemContainer.serviceLineItemVASNVParameters[j].serviceLineItemVASNVParameter.value = receivedLineItemContainer.serviceLineItemVASNVParameters[i].serviceLineItemVASNVParameter.value;
                    existingLineItemContainer.serviceLineItemVASNVParameters[j].serviceLineItemVASNVParameter.nvAction = receivedLineItemContainer.serviceLineItemVASNVParameters[i].serviceLineItemVASNVParameter.nvAction;
                }
            }
        }

        if(!found) {
            var position = (existingLineItemContainer.serviceLineItemVASNVParameters != null ? existingLineItemContainer.serviceLineItemVASNVParameters.length : 0);
            var containerItemPath = (existingLineItemContainer.serviceLineItemVASNVParameters != null ? existingLineItemContainer.serviceLineItemVASNVParameters.orderDataPath : (existingLineItemContainer.orderDataPath + ".serviceLineItemVASNVParameters"));
            var vasDoc = existingOrder.createOrderItemByPath(containerItemPath + "." + (position+1) + ".serviceLineItemVASNVParameter");
            vasDoc.name = receivedLineItemContainer.serviceLineItemVASNVParameters[i].serviceLineItemVASNVParameter.name;
            vasDoc.value = receivedLineItemContainer.serviceLineItemVASNVParameters[i].serviceLineItemVASNVParameter.value;
            vasDoc.nvAction = receivedLineItemContainer.serviceLineItemVASNVParameters[i].serviceLineItemVASNVParameter.nvAction;
            vasDoc.isVAS = "Y";
            vasDoc.parentElementId = existingLineItemContainer.serviceLineItem.id;
        }
    }
}

return validationErrors;
