// nv check; it loops in existing NV pair and verifies if it exists also in received one
if(receivedLineItemContainer.subSubServiceLineItemNameValueParameters != null) {
    for(var i=0; i<receivedLineItemContainer.subSubServiceLineItemNameValueParameters.length; i++) {
        var receivedNVName = receivedLineItemContainer.subSubServiceLineItemNameValueParameters[i].subSubServiceLineItemNameValueParameter.name;
        var found = false;

        if(existingLineItemContainer.subSubServiceLineItemNameValueParameters != null) {
            for(var j=0; j<existingLineItemContainer.subSubServiceLineItemNameValueParameters.length && !found; j++) {
                if(receivedNVName == existingLineItemContainer.subSubServiceLineItemNameValueParameters[j].subSubServiceLineItemNameValueParameter.name) {
                    // found; comparing the values
                    found = true;
                    existingLineItemContainer.subSubServiceLineItemNameValueParameters[j].subSubServiceLineItemNameValueParameter.value = receivedLineItemContainer.subSubServiceLineItemNameValueParameters[i].subSubServiceLineItemNameValueParameter.value;
                    existingLineItemContainer.subSubServiceLineItemNameValueParameters[j].subSubServiceLineItemNameValueParameter.nvAction = receivedLineItemContainer.subSubServiceLineItemNameValueParameters[i].subSubServiceLineItemNameValueParameter.nvAction;
                }
            }
        }

        if(!found) {
            var position = (existingLineItemContainer.subSubServiceLineItemNameValueParameters != null ? existingLineItemContainer.subSubServiceLineItemNameValueParameters.length : 0);
            var containerItemPath = (existingLineItemContainer.subSubServiceLineItemNameValueParameters != null ? existingLineItemContainer.subSubServiceLineItemNameValueParameters.orderDataPath : (existingLineItemContainer.orderDataPath + ".subSubServiceLineItemNameValueParameters"));
            var nvDoc = existingOrder.createOrderItemByPath(containerItemPath + "." + (position+1) + ".subSubServiceLineItemNameValueParameter");
            nvDoc.name = receivedLineItemContainer.subSubServiceLineItemNameValueParameters[i].subSubServiceLineItemNameValueParameter.name;
            nvDoc.value = receivedLineItemContainer.subSubServiceLineItemNameValueParameters[i].subSubServiceLineItemNameValueParameter.value;
            nvDoc.nvAction = receivedLineItemContainer.subSubServiceLineItemNameValueParameters[i].subSubServiceLineItemNameValueParameter.nvAction;
            nvDoc.parentElementId = existingLineItemContainer.subSubServiceLineItem.id;
        }
    }
}

