menuObject.startDateTime = Global.getSessionParameter("Dashboard_StartTime");
if(menuObject.startDateTime == null) {
    var date = new Date();

    menuObject.startDateTime = new Date(date.getFullYear(), date.getMonth()-1, date.getDate());
    debugPrintln("startDateTime = " + menuObject.startDateTime);
    debugPrintln("startDateTime.month = " + menuObject.startDateTime.getMonth());
}
menuObject.endDateTime = Global.getSessionParameter("Dashboard_EndTime");

menuObject.refreshTime = Global.getSessionParameter("Dashboard_RefreshTime");
if(menuObject.refreshTime == null) {
    menuObject.refreshTime = 1;
}

