debugPrintln(stcc.getSysdateForLog() + "StartProcessForMigratedOrdersInProv - Start");
var startTime = new Date();

var orderIdsFound = Finder.runFinder("crmToSOMMigration.findMigratedOrderIdsInProvisioning", "select", null);
// note: the finder returns a document whose leaf name is "cwDocID" but it contains the cwOrderId!!!!

var found = 0;
var failed = 0;
var processedSuccessfully = 0;

if(orderIdsFound != null) {
    found = orderIdsFound.length;
    for(var i=0; i<orderIdsFound.length; i++) {
        try {
            var order = Order.getOrderById(orderIdsFound[i].cwDocID);
            if(order!= null) {
                debugPrintln(stcc.getSysdateForLog() + "StartProcessForMigratedOrdersInProv - Starting OrchestrationProcess For Order " + orderIdsFound[i].cwDocID);
                var processId = Process.startProcess ("stcw.mainSTCWOrchestrationProcess", orderIdsFound[i].cwDocID, orderIdsFound[i].cwDocID);
                debugPrintln(stcc.getSysdateForLog() + "StartProcessForMigratedOrdersInProv - Started OrchestrationProcess For Order " + orderIdsFound[i].cwDocID + "; processId = " + processId);
                processedSuccessfully++;
            }
            else {
                debugPrintln(stcc.getSysdateForLog() + "StartProcessForMigratedOrdersInProv - Unable to load the order " + orderIdsFound[i].cwDocID + "!");
            }
        }
        catch(exc) {
debugPrintln(stcc.getSysdateForLog() + "StartProcessForMigratedOrdersInProv - Order.id: " + orderIdsFound[i].cwDocID + " - Exc: " + exc.message);
            failed++;
        }
    }
}

var endTime = new Date();
var totalTime = (endTime.getTime() - startTime.getTime())/1000;

var msg = "Start Process For Migrated Orders In Provisioning - Execution Completed\n" + "Statistics:\n" +
          "  Orders Found: " + found + "\n" +
          "  Orders Failed to load: " + failed + "\n" +
          "  Orders Processed Successfully: " + processedSuccessfully + "\n\n\n" +
          "Total Time: " + totalTime + " secs.";
debugPrintln(stcc.getSysdateForLog() + " StartProcessForMigratedOrdersInProv - " + msg);
Global.showUserMessage(msg);

var debugMsg = " SendQSUForSync - ";
debugPrintln(stcc.getSysdateForLog() + debugMsg + "Start");

var statistics = crmToSOMMigration.sendQSUForSync();

var msg = "Send QSU For Sync - Execution Completed\n" + "Statistics:\n" +
          "  Records Found: " + statistics[0] + "\n" +
          "  Processed Successfully: " + statistics[1] + "\n" +
          "  Processed With Failure: " + (statistics[0] - statistics[1]);
debugPrintln(stcc.getSysdateForLog() + debugMsg + msg);
Global.showUserMessage(msg);

debugPrintln(stcc.getSysdateForLog() + " deleteMigratedOrders - Start");
var startTime = new Date();

var inputDoc = new Document("stcw.search_bundleOrder");
inputDoc.isMigrated = true;

var orderListFound = Finder.runFinder("stcw.findAllSTCBundleOrders_Script", "select", inputDoc);

var countDeleted = 0;
if(orderListFound != null && orderListFound.length > 0) {
    var limit = (orderListFound.length > 5000 ? 5000 : orderListFound.length);

    for(var i=0; i<limit; i++) {
        var order = Order.getOrderById(orderListFound[i].id);
        order.deleteOrder();

        var orderOrchestrationList = stcw.findRecordsInOrchTabByOrderId_ParentId(order.id);
        if(orderOrchestrationList != null && orderOrchestrationList.length > 0) {
            for(var j=0; j<orderOrchestrationList.length; j++) {
                orderOrchestrationList[j].deleteItem();
            }
        }

        countDeleted++;
    }
}

var endTime = new Date();
var totalTime = (endTime.getTime() - startTime.getTime())/1000;

var msg = "Delete Migrated Orders - Execution Completed\n" +
          "  Deleted: " + countDeleted + "\n\n\n" +
          "Total Time: " + totalTime + " secs. ";
debugPrintln(stcc.getSysdateForLog() + " deleteMigratedOrders - " + msg);
Global.showUserMessage(msg);

debugPrintln(stcc.getSysdateForLog() + "TerminateProcessesForOrders_Quotes - Start");
var startTime = new Date();

var processesFound = Finder.runFinder("crmToSOMMigration.findAllRunningProcessesForOrders_Quotes", "select", null);

var found = 0;
var failed = 0;
var processedSuccessfully = 0;

if(processesFound != null) {
    found = processesFound.length;
    for(var i=0; i<processesFound.length; i++) {
        debugPrintln(stcc.getSysdateForLog() + "TerminateProcessesForOrders_Quotes - Terminating Process " + processesFound[i].id);

        try {
            if(processesFound[i].STATUS == 8) {
                Process.resumeAllStaleProcesses(processesFound[i].id);
            }

            Process.terminateProcess(processesFound[i].id);
            processedSuccessfully++;
        }
        catch(exc) {
debugPrintln(stcc.getSysdateForLog() + "TerminateProcessesForOrders_Quotes - ProcessId: " + processesFound[i].id + " - Exc: " + exc.message);
            failed++;
        }
    }
}

var endTime = new Date();
var totalTime = (endTime.getTime() - startTime.getTime())/1000;

var msg = "Terminate Processes For Orders & Quotes - Execution Completed\n" + "Statistics:\n" +
          "  Orders Found: " + found + "\n" +
          "  Failed to load: " + failed + "\n" +
          "  Processed Successfully: " + processedSuccessfully + "\n\n\n" +
          "Total Time: " + totalTime + " secs.";
debugPrintln(stcc.getSysdateForLog() + " TerminateProcessesForOrders_Quotes - " + msg);
Global.showUserMessage(msg);

var debugMsg = " SendOSUForSync - ";
debugPrintln(stcc.getSysdateForLog() + debugMsg + "Start");

var statistics = crmToSOMMigration.sendOSUForSync();

var msg = "Send OSU For Sync - Execution Completed\n" + "Statistics:\n" +
          "  Records Found: " + statistics[0] + "\n" +
          "  Processed Successfully: " + statistics[1] + "\n" +
          "  Processed With Failure: " + (statistics[0] - statistics[1]);
debugPrintln(stcc.getSysdateForLog() + debugMsg + msg);
Global.showUserMessage(msg);

